"use strict";const title="Mario Krok-Adamski - Recruitment task",popup=(document.querySelector("title").textContent=title,document.querySelector(".popup")),popupContainer=document.querySelector(".popup-container"),popupParagraph=document.querySelector(".popup-paragraph"),btnOpenPopup=document.querySelector(".button-popup"),btnClosePopup=document.querySelector(".close-popup");let userClickCount=0;btnOpenPopup.addEventListener("click",()=>{popup.classList.remove("hidden"),userClickCount+=1,popupParagraph.textContent=`You have clicked ${userClickCount} times to related button.`});const closePopup=()=>{popup.classList.add("hidden")};btnClosePopup.addEventListener("click",()=>{closePopup()}),document.addEventListener("click",e=>{e.target.classList.contains("popup")&&closePopup()}),document.addEventListener("keydown",e=>{"Escape"===e.key&&closePopup()});